import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import {
  Container,
  Box,
  Paper,
  Typography,
  Avatar,
  Button,
  Grid,
  Card,
  CardContent,
  CardActions,
  IconButton,
  Divider,
  CircularProgress,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
} from '@mui/material';
import {
  Edit as EditIcon,
  ThumbUp as ThumbUpIcon,
  ThumbUpOutlined as ThumbUpOutlinedIcon,
  Comment as CommentIcon,
  Share as ShareIcon,
} from '@mui/icons-material';
import { users, posts } from '../services/api';
import { useAuth } from '../context/AuthContext';

const Profile = () => {
  const { id } = useParams();
  const { user: currentUser } = useAuth();
  const queryClient = useQueryClient();
  const [editDialogOpen, setEditDialogOpen] = useState(false);
  const [editForm, setEditForm] = useState({
    username: '',
    bio: '',
  });

  const { data: profileData, isLoading: profileLoading } = useQuery({
    queryKey: ['user', id],
    queryFn: () => users.getById(id),
  });

  const { data: userPosts, isLoading: postsLoading } = useQuery({
    queryKey: ['userPosts', id],
    queryFn: () => posts.getAll({ author: id }),
  });

  const followMutation = useMutation({
    mutationFn: () => users.follow(id),
    onSuccess: () => {
      queryClient.invalidateQueries(['user', id]);
    },
  });

  const updateProfileMutation = useMutation({
    mutationFn: (data) => users.update(id, data),
    onSuccess: () => {
      queryClient.invalidateQueries(['user', id]);
      setEditDialogOpen(false);
    },
  });

  const likePostMutation = useMutation({
    mutationFn: (postId) => posts.like(postId),
    onSuccess: () => {
      queryClient.invalidateQueries(['userPosts', id]);
    },
  });

  const handleEditProfile = () => {
    setEditForm({
      username: profileData.data.username,
      bio: profileData.data.bio || '',
    });
    setEditDialogOpen(true);
  };

  const handleUpdateProfile = () => {
    updateProfileMutation.mutate(editForm);
  };

  const handleLike = (postId) => {
    likePostMutation.mutate(postId);
  };

  if (profileLoading || postsLoading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
        <CircularProgress />
      </Box>
    );
  }

  const isOwnProfile = currentUser._id === id;
  const isFollowing = profileData.data.followers.includes(currentUser._id);

  return (
    <Container maxWidth="md">
      <Paper sx={{ p: 3, mb: 3 }}>
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
          <Avatar
            src={profileData.data.avatar}
            alt={profileData.data.username}
            sx={{ width: 100, height: 100, mr: 3 }}
          />
          <Box sx={{ flexGrow: 1 }}>
            <Typography variant="h4" gutterBottom>
              {profileData.data.username}
            </Typography>
            <Typography variant="body1" color="text.secondary" paragraph>
              {profileData.data.bio || 'No bio yet'}
            </Typography>
            <Box sx={{ display: 'flex', gap: 2 }}>
              <Typography variant="body2">
                <strong>{profileData.data.followers.length}</strong> followers
              </Typography>
              <Typography variant="body2">
                <strong>{profileData.data.following.length}</strong> following
              </Typography>
            </Box>
          </Box>
          {isOwnProfile ? (
            <Button
              variant="outlined"
              startIcon={<EditIcon />}
              onClick={handleEditProfile}
            >
              Edit Profile
            </Button>
          ) : (
            <Button
              variant={isFollowing ? 'outlined' : 'contained'}
              onClick={() => followMutation.mutate()}
              disabled={followMutation.isLoading}
            >
              {isFollowing ? 'Unfollow' : 'Follow'}
            </Button>
          )}
        </Box>
      </Paper>

      <Grid container spacing={3}>
        {userPosts?.data?.map((post) => (
          <Grid item xs={12} key={post._id}>
            <Card>
              <CardContent>
                <Typography variant="body1" sx={{ mb: 2 }}>
                  {post.content}
                </Typography>
                {post.media && (
                  <Box
                    component="img"
                    src={post.media}
                    alt="Post media"
                    sx={{
                      width: '100%',
                      maxHeight: 400,
                      objectFit: 'cover',
                      borderRadius: 1,
                    }}
                  />
                )}
              </CardContent>
              <Divider />
              <CardActions>
                <IconButton
                  onClick={() => handleLike(post._id)}
                  color={post.likes.includes(currentUser._id) ? 'primary' : 'default'}
                >
                  {post.likes.includes(currentUser._id) ? (
                    <ThumbUpIcon />
                  ) : (
                    <ThumbUpOutlinedIcon />
                  )}
                </IconButton>
                <Typography variant="body2" color="text.secondary">
                  {post.likes.length}
                </Typography>
                <IconButton>
                  <CommentIcon />
                </IconButton>
                <Typography variant="body2" color="text.secondary">
                  {post.comments.length}
                </Typography>
                <IconButton>
                  <ShareIcon />
                </IconButton>
              </CardActions>
            </Card>
          </Grid>
        ))}
      </Grid>

      <Dialog open={editDialogOpen} onClose={() => setEditDialogOpen(false)}>
        <DialogTitle>Edit Profile</DialogTitle>
        <DialogContent>
          <TextField
            fullWidth
            label="Username"
            value={editForm.username}
            onChange={(e) =>
              setEditForm((prev) => ({ ...prev, username: e.target.value }))
            }
            margin="normal"
          />
          <TextField
            fullWidth
            label="Bio"
            value={editForm.bio}
            onChange={(e) =>
              setEditForm((prev) => ({ ...prev, bio: e.target.value }))
            }
            margin="normal"
            multiline
            rows={4}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setEditDialogOpen(false)}>Cancel</Button>
          <Button
            onClick={handleUpdateProfile}
            variant="contained"
            disabled={updateProfileMutation.isLoading}
          >
            Save
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default Profile;
