import React, { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import {
  Container,
  Box,
  Paper,
  TextField,
  Button,
  Typography,
  Card,
  CardContent,
  CardActions,
  IconButton,
  Avatar,
  Divider,
  CircularProgress,
  Alert,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  Grid,
  Menu,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  List,
  ListItem,
  ListItemText,
} from '@mui/material';
import {
  ThumbUp as ThumbUpIcon,
  ThumbUpOutlined as ThumbUpOutlinedIcon,
  Comment as CommentIcon,
  Share as ShareIcon,
  Image as ImageIcon,
  Close as CloseIcon,
} from '@mui/icons-material';
import { posts, groups, users } from '../services/api';
import { useAuth } from '../context/AuthContext';

const Home = () => {
  const { user } = useAuth();
  const queryClient = useQueryClient();
  const [newPost, setNewPost] = useState('');
  const [imageFile, setImageFile] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [anchorEl, setAnchorEl] = useState(null);
  const [selectedGroup, setSelectedGroup] = useState('');
  const [error, setError] = useState('');
  const [groupDialogOpen, setGroupDialogOpen] = useState(false);

  const { data: postsData, isLoading: postsLoading } = useQuery({
    queryKey: ['posts'],
    queryFn: () => posts.getAll(),
  });

  const { data: groupsData, isLoading: groupsLoading } = useQuery({
    queryKey: ['groups'],
    queryFn: () => groups.getAll(),
  });

  const createPostMutation = useMutation({
    mutationFn: (postData) => posts.create(postData),
    onSuccess: () => {
      queryClient.invalidateQueries(['posts']);
      setNewPost('');
      setImageFile(null);
      setImagePreview(null);
      setSelectedGroup('');
      setError('');
    },
    onError: (error) => {
      setError(error.response?.data?.message || 'Failed to create post');
    },
  });

  const likePostMutation = useMutation({
    mutationFn: (postId) => posts.like(postId),
    onSuccess: () => {
      queryClient.invalidateQueries(['posts']);
    },
  });

  const handlePostSubmit = (e) => {
    e.preventDefault();
    if (!newPost.trim() && !imageFile) return;

    const formData = new FormData();
    formData.append('content', newPost);
    if (imageFile) {
      formData.append('media', imageFile);
    }
    if (selectedGroup) {
      formData.append('group', selectedGroup);
    }

    createPostMutation.mutate(formData);
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImageFile(file);
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleImageClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleImageMenuClose = () => {
    setAnchorEl(null);
  };

  const handleImageUpload = () => {
    document.getElementById('image-upload').click();
    handleImageMenuClose();
  };

  const handleRemoveImage = () => {
    setImageFile(null);
    setImagePreview(null);
    handleImageMenuClose();
  };

  const handleGroupSelect = (groupId) => {
    setSelectedGroup(groupId);
    setGroupDialogOpen(false);
  };

  const handleLike = (postId) => {
    likePostMutation.mutate(postId);
  };

  if (postsLoading || groupsLoading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
        <CircularProgress />
      </Box>
    );
  }

  const groupsList = groupsData?.data || [];
  const postsList = Array.isArray(postsData?.data) ? postsData.data : [];

  return (
    <Container maxWidth="md">
      <Paper sx={{ p: 2, mb: 3 }}>
        <Box component="form" onSubmit={handlePostSubmit}>
          {error && (
            <Alert severity="error" sx={{ mb: 2 }}>
              {error}
            </Alert>
          )}
          <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
            <Button
              size="small"
              variant="outlined"
              onClick={() => setGroupDialogOpen(true)}
              sx={{ mr: 2 }}
            >
              {selectedGroup ? 'Change Group' : 'Choose Group'}
            </Button>
            {selectedGroup && (
              <Typography variant="body2" color="text.secondary">
                Group: {groupsList.find(g => g._id === selectedGroup)?.name}
              </Typography>
            )}
          </Box>
          <TextField
            fullWidth
            multiline
            rows={3}
            placeholder="What's on your mind?"
            value={newPost}
            onChange={(e) => setNewPost(e.target.value)}
            sx={{ mb: 2 }}
          />
          {imagePreview && (
            <Box sx={{ position: 'relative', mb: 2 }}>
              <img
                src={imagePreview}
                alt="Preview"
                style={{
                  maxWidth: '100%',
                  maxHeight: '300px',
                  borderRadius: '8px',
                }}
              />
              <IconButton
                size="small"
                onClick={handleRemoveImage}
                sx={{
                  position: 'absolute',
                  top: 8,
                  right: 8,
                  bgcolor: 'rgba(0, 0, 0, 0.5)',
                  '&:hover': { bgcolor: 'rgba(0, 0, 0, 0.7)' },
                }}
              >
                <CloseIcon sx={{ color: 'white' }} />
              </IconButton>
            </Box>
          )}
          <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
            <Box>
              <input
                type="file"
                id="image-upload"
                accept="image/*"
                onChange={handleImageChange}
                style={{ display: 'none' }}
              />
              <IconButton onClick={handleImageClick}>
                <ImageIcon />
              </IconButton>
              <Menu
                anchorEl={anchorEl}
                open={Boolean(anchorEl)}
                onClose={handleImageMenuClose}
              >
                <MenuItem onClick={handleImageUpload}>Upload Image</MenuItem>
                {imagePreview && (
                  <MenuItem onClick={handleRemoveImage}>Remove Image</MenuItem>
                )}
              </Menu>
            </Box>
            <Button
              type="submit"
              variant="contained"
              disabled={createPostMutation.isLoading || (!newPost.trim() && !imageFile)}
            >
              Post
            </Button>
          </Box>
        </Box>
      </Paper>

      <Grid container spacing={3}>
        {postsList.map((post) => (
          <Grid item xs={12} key={post._id}>
            <Card>
              <CardContent>
                <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                  <Avatar
                    src={post.author?.profilePicture}
                    alt={post.author?.username}
                    sx={{ mr: 2 }}
                  />
                  <Box>
                    <Typography variant="subtitle1">
                      {post.author?.username}
                    </Typography>
                    <Typography variant="caption" color="text.secondary">
                      {new Date(post.createdAt).toLocaleString()}
                    </Typography>
                  </Box>
                </Box>
                <Typography variant="body1" sx={{ mb: 2 }}>
                  {post.content}
                </Typography>
                {post.media && (
                  <Box
                    component="img"
                    src={post.media}
                    alt="Post media"
                    sx={{
                      width: '100%',
                      maxHeight: 400,
                      objectFit: 'cover',
                      borderRadius: 1,
                    }}
                  />
                )}
              </CardContent>
              <Divider />
              <CardActions>
                <IconButton
                  onClick={() => handleLike(post._id)}
                  color={post.likes?.includes(user?._id) ? 'primary' : 'default'}
                >
                  {post.likes?.includes(user?._id) ? (
                    <ThumbUpIcon />
                  ) : (
                    <ThumbUpOutlinedIcon />
                  )}
                </IconButton>
                <Typography variant="body2" color="text.secondary">
                  {post.likes?.length || 0}
                </Typography>
                <IconButton>
                  <CommentIcon />
                </IconButton>
                <Typography variant="body2" color="text.secondary">
                  {post.comments?.length || 0}
                </Typography>
                <IconButton>
                  <ShareIcon />
                </IconButton>
              </CardActions>
            </Card>
          </Grid>
        ))}
      </Grid>

      <Dialog open={groupDialogOpen} onClose={() => setGroupDialogOpen(false)}>
        <DialogTitle>Choose a Group</DialogTitle>
        <DialogContent>
          <List>
            {groupsList.map((group) => (
              <ListItem button key={group._id} onClick={() => handleGroupSelect(group._id)}>
                <ListItemText primary={group.name} />
              </ListItem>
            ))}
          </List>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setGroupDialogOpen(false)}>Cancel</Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default Home;
