import { useState } from 'react';
import users from '../services/api';

const useNotifications = (userId) => {
  const [notifications, setNotifications] = useState([]);
  const [unreadCount, setUnreadCount] = useState(0);

  // Fetch notifications from the API (friend requests)
  const fetchNotifications = async () => {
    if (!userId) return;
    try {
      const [incomingRes, outgoingRes] = await Promise.all([
        users.getFriendRequests(),
        users.getOutgoingFriendRequests()
      ]);
      const incoming = (incomingRes?.data?.data || []).map(req => ({
        type: 'friend_request_incoming',
        from: req.sender,
        createdAt: req.createdAt,
        status: req.status,
        _id: req._id,
        read: false,
        message: `${req.sender?.username || 'Someone'} sent you a friend request`,
        avatar: req.sender?.profilePicture || '/default-profile.png',
      }));
      const outgoing = (outgoingRes?.data?.data || []).map(req => ({
        type: 'friend_request_outgoing',
        to: req.receiver,
        createdAt: req.createdAt,
        status: req.status,
        _id: req._id,
        read: false,
        message: `You sent a friend request to ${req.receiver?.username || 'someone'}`,
        avatar: req.receiver?.profilePicture || '/default-profile.png',
      }));
      const all = [...incoming, ...outgoing].sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
      setNotifications(all);
      setUnreadCount(all.length); // You can add logic to track read/unread if needed
    } catch (err) {
      setNotifications([]);
      setUnreadCount(0);
    }
  };

  const markAsRead = () => {
    setUnreadCount(0);
  };

  const markAllAsRead = () => {
    setNotifications((prev) =>
      prev.map((notification) => ({ ...notification, read: true }))
    );
  };

  const clearNotification = (notificationId) => {
    setNotifications((prev) =>
      prev.filter((notification) => notification._id !== notificationId)
    );
  };

  const clearAllNotifications = () => {
    setNotifications([]);
  };

  return {
    notifications,
    unreadCount,
    fetchNotifications,
    markAsRead,
    markAllAsRead,
    clearNotification,
    clearAllNotifications,
  };
};

export default useNotifications;
