import React, { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import {
  Container,
  Box,
  Paper,
  TextField,
  Button,
  Typography,
  Card,
  CardContent,
  CardActions,
  IconButton,
  Avatar,
  Divider,
  CircularProgress,
  Alert,
} from '@mui/material';
import {
  ThumbUp as ThumbUpIcon,
  ThumbUpOutlined as ThumbUpOutlinedIcon,
  Comment as CommentIcon,
  Share as ShareIcon,
} from '@mui/icons-material';
import { posts } from '../services/api';
import { useAuth } from '../context/AuthContext';

const Home = () => {
  const { user } = useAuth();
  const queryClient = useQueryClient();
  const [newPost, setNewPost] = useState('');
  const [comment, setComment] = useState('');
  const [error, setError] = useState('');

  const { data: postsData, isLoading } = useQuery({
    queryKey: ['posts'],
    queryFn: () => posts.getAll(),
  });

  const createPostMutation = useMutation({
    mutationFn: (content) => posts.create({ content }),
    onSuccess: () => {
      queryClient.invalidateQueries(['posts']);
      setNewPost('');
      setError('');
    },
    onError: (error) => {
      setError(error.response?.data?.message || 'Failed to create post');
    },
  });

  const likePostMutation = useMutation({
    mutationFn: (postId) => posts.like(postId),
    onSuccess: () => {
      queryClient.invalidateQueries(['posts']);
    },
  });

  const handleCreatePost = (e) => {
    e.preventDefault();
    setError('');
    if (newPost.trim()) {
      createPostMutation.mutate(newPost.trim());
    }
  };

  const handleLike = (postId) => {
    likePostMutation.mutate(postId);
  };

  if (isLoading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Container maxWidth="md">
      <Paper sx={{ p: 2, mb: 3 }}>
        <Box component="form" onSubmit={handleCreatePost}>
          {error && (
            <Alert severity="error" sx={{ mb: 2 }}>
              {error}
            </Alert>
          )}
          <TextField
            fullWidth
            multiline
            rows={3}
            placeholder="What's on your mind?"
            value={newPost}
            onChange={(e) => setNewPost(e.target.value)}
            sx={{ mb: 2 }}
          />
          <Button
            variant="contained"
            type="submit"
            disabled={!newPost.trim() || createPostMutation.isLoading}
          >
            {createPostMutation.isLoading ? 'Posting...' : 'Post'}
          </Button>
        </Box>
      </Paper>

      {postsData?.data && Array.isArray(postsData.data) && postsData.data.map((post) => (
        <Card key={post._id} sx={{ mb: 2 }}>
          <CardContent>
            <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
              <Avatar
                src={post.author?.profilePicture}
                alt={post.author?.username}
                sx={{ mr: 2 }}
              />
              <Box>
                <Typography variant="subtitle1">
                  {post.author?.username}
                </Typography>
                <Typography variant="caption" color="text.secondary">
                  {new Date(post.createdAt).toLocaleString()}
                </Typography>
              </Box>
            </Box>
            <Typography variant="body1" sx={{ mb: 2 }}>
              {post.content}
            </Typography>
            {post.media && (
              <Box
                component="img"
                src={post.media}
                alt="Post media"
                sx={{
                  width: '100%',
                  maxHeight: 400,
                  objectFit: 'cover',
                  borderRadius: 1,
                }}
              />
            )}
          </CardContent>
          <Divider />
          <CardActions>
            <IconButton
              onClick={() => handleLike(post._id)}
              color={post.likes?.includes(user?._id) ? 'primary' : 'default'}
            >
              {post.likes?.includes(user?._id) ? (
                <ThumbUpIcon />
              ) : (
                <ThumbUpOutlinedIcon />
              )}
            </IconButton>
            <Typography variant="body2" color="text.secondary">
              {post.likes?.length || 0}
            </Typography>
            <IconButton>
              <CommentIcon />
            </IconButton>
            <Typography variant="body2" color="text.secondary">
              {post.comments?.length || 0}
            </Typography>
            <IconButton>
              <ShareIcon />
            </IconButton>
          </CardActions>
          {post.comments?.length > 0 && (
            <Box sx={{ p: 2 }}>
              <Typography variant="subtitle2" sx={{ mb: 1 }}>
                Comments
              </Typography>
              {post.comments.map((comment) => (
                <Box key={comment._id} sx={{ display: 'flex', mb: 1 }}>
                  <Avatar
                    src={comment.author?.profilePicture}
                    alt={comment.author?.username}
                    sx={{ width: 24, height: 24, mr: 1 }}
                  />
                  <Box>
                    <Typography variant="subtitle2">
                      {comment.author?.username}
                    </Typography>
                    <Typography variant="body2">{comment.content}</Typography>
                  </Box>
                </Box>
              ))}
            </Box>
          )}
        </Card>
      ))}
    </Container>
  );
};

export default Home;
