import React, { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import {
  Box,
  TextField,
  Button,
  List,
  ListItem,
  ListItemAvatar,
  ListItemText,
  Avatar,
  IconButton,
  Paper,
  Typography,
  CircularProgress,
  Alert,
} from '@mui/material';
import {
  Person as PersonIcon,
  PersonAdd as PersonAddIcon,
} from '@mui/icons-material';
import { users } from '../services/api';
import { useAuth } from '../context/AuthContext';

const AddFriend = ({ currentUser }) => {
  const { user } = useAuth();
  const queryClient = useQueryClient();
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const searchMutation = useMutation({
    mutationFn: async (query) => {
      console.log('Searching for:', query);
      const response = await users.search(query);
      console.log('Raw search response:', response);
      return response;
    },
    onSuccess: (data) => {
      console.log('Search mutation success:', data);
      setSearchResults(data);
      setError(null);
    },
    onError: (error) => {
      console.error('Search error:', error);
      setError(error.response?.data?.error || 'Error searching users');
    }
  });

  const sendRequestMutation = useMutation({
    mutationFn: ({ userId, fromUserId }) => users.sendFriendRequest(userId, fromUserId),
    onSuccess: (data) => {
      console.log('Friend request sent successfully:', data);
      setError(null);
      // Optionally refresh search results
      if (searchQuery) {
        searchMutation.mutate(searchQuery);
      }
    },
    onError: (error) => {
      console.error('Send request error:', error);
      setError(error.response?.data?.error || 'Error sending friend request');
    }
  });

  const handleSearch = (e) => {
    if (e.key === 'Enter' && searchQuery.trim().length >= 3) {
      e.preventDefault();
      console.log('Triggering search for:', searchQuery);
      setLoading(true);
      searchMutation.mutate(searchQuery, {
        onSettled: () => setLoading(false)
      });
    }
  };

  const handleSendRequest = (userId) => {
    console.log('handleSendRequest called with:', { userId, currentUser, searchResults });
    const fromUserId = currentUser?._id || 'temp-' + Date.now();
    console.log('Sending friend request with:', { userId, fromUserId });
    sendRequestMutation.mutate({ userId, fromUserId });
  };

  // Filter out current user from search results
  const usersList = searchResults?.data?.filter(user => user._id !== currentUser?._id) || [];
  console.log('Current search results:', searchResults);
  console.log('Filtered users list:', usersList);

  return (
    <Box sx={{ p: 2 }}>
      <Typography variant="h6" gutterBottom>
        Add Friends
      </Typography>
      <TextField
        fullWidth
        variant="outlined"
        placeholder="Search by username..."
        value={searchQuery}
        onChange={(e) => {
          setSearchQuery(e.target.value);
          setError(null);
        }}
        onKeyDown={handleSearch}
        sx={{ mb: 2 }}
      />
      {(error || searchMutation.error) && (
        <Alert severity="error" sx={{ mb: 2 }}>
          {error || searchMutation.error?.message || 'An error occurred'}
        </Alert>
      )}
      {loading ? (
        <Box sx={{ display: 'flex', justifyContent: 'center', p: 2 }}>
          <CircularProgress />
        </Box>
      ) : searchQuery.length >= 3 ? (
        <List>
          {usersList.map((user) => (
            <ListItem
              key={user._id}
              secondaryAction={
                <IconButton
                  edge="end"
                  aria-label="add friend"
                  onClick={() => handleSendRequest(user._id)}
                  disabled={sendRequestMutation.isLoading}
                >
                  <PersonAddIcon />
                </IconButton>
              }
            >
              <ListItemAvatar>
                <Avatar
                  src={user.profilePicture ? `http://localhost:5000${user.profilePicture}` : undefined}
                  alt={user.username}
                >
                  <PersonIcon />
                </Avatar>
              </ListItemAvatar>
              <ListItemText
                primary={user.username}
                secondary={user.email}
              />
            </ListItem>
          ))}
          {usersList.length === 0 && searchQuery.length >= 3 && (
            <ListItem>
              <ListItemText primary="No users found" />
            </ListItem>
          )}
        </List>
      ) : searchQuery.length > 0 && searchQuery.length < 3 ? (
        <Typography color="text.secondary" sx={{ p: 2 }}>
          Type at least 3 characters to search
        </Typography>
      ) : null}
    </Box>
  );
};

export default AddFriend;
