import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import {
  Container,
  Box,
  Paper,
  Typography,
  Button,
  Grid,
  Card,
  CardContent,
  CardActions,
  IconButton,
  Divider,
  CircularProgress,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  Avatar,
  List,
  ListItem,
  ListItemAvatar,
  ListItemText,
} from '@mui/material';
import {
  Edit as EditIcon,
  ThumbUp as ThumbUpIcon,
  ThumbUpOutlined as ThumbUpOutlinedIcon,
  Comment as CommentIcon,
  Share as ShareIcon,
  Person as PersonIcon,
} from '@mui/icons-material';
import { groups, posts } from '../services/api';
import { useAuth } from '../context/AuthContext';

const Group = () => {
  const { id } = useParams();
  const { user: currentUser } = useAuth();
  const queryClient = useQueryClient();
  const [editDialogOpen, setEditDialogOpen] = useState(false);
  const [newPost, setNewPost] = useState('');
  const [editForm, setEditForm] = useState({
    name: '',
    description: '',
  });

  const { data: groupData, isLoading: groupLoading } = useQuery({
    queryKey: ['group', id],
    queryFn: () => groups.getById(id),
  });

  const { data: groupPosts, isLoading: postsLoading } = useQuery({
    queryKey: ['groupPosts', id],
    queryFn: () => posts.getAll({ group: id }),
  });

  const joinGroupMutation = useMutation({
    mutationFn: () => groups.join(id),
    onSuccess: () => {
      queryClient.invalidateQueries(['group', id]);
    },
  });

  const updateGroupMutation = useMutation({
    mutationFn: (data) => groups.update(id, data),
    onSuccess: () => {
      queryClient.invalidateQueries(['group', id]);
      setEditDialogOpen(false);
    },
  });

  const createPostMutation = useMutation({
    mutationFn: (content) => posts.create({ content, group: id }),
    onSuccess: () => {
      queryClient.invalidateQueries(['groupPosts', id]);
      setNewPost('');
    },
  });

  const likePostMutation = useMutation({
    mutationFn: (postId) => posts.like(postId),
    onSuccess: () => {
      queryClient.invalidateQueries(['groupPosts', id]);
    },
  });

  const handleEditGroup = () => {
    setEditForm({
      name: groupData.data.name,
      description: groupData.data.description || '',
    });
    setEditDialogOpen(true);
  };

  const handleUpdateGroup = () => {
    updateGroupMutation.mutate(editForm);
  };

  const handleCreatePost = (e) => {
    e.preventDefault();
    if (newPost.trim()) {
      createPostMutation.mutate(newPost);
    }
  };

  const handleLike = (postId) => {
    likePostMutation.mutate(postId);
  };

  if (groupLoading || postsLoading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
        <CircularProgress />
      </Box>
    );
  }

  const isAdmin = groupData.data.admin === currentUser._id;
  const isMember = groupData.data.members.includes(currentUser._id);

  return (
    <Container maxWidth="md">
      <Paper sx={{ p: 3, mb: 3 }}>
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
          <Box sx={{ flexGrow: 1 }}>
            <Typography variant="h4" gutterBottom>
              {groupData.data.name}
            </Typography>
            <Typography variant="body1" color="text.secondary" paragraph>
              {groupData.data.description}
            </Typography>
            <Box sx={{ display: 'flex', gap: 2 }}>
              <Typography variant="body2">
                <strong>{groupData.data.members.length}</strong> members
              </Typography>
            </Box>
          </Box>
          {isAdmin ? (
            <Button
              variant="outlined"
              startIcon={<EditIcon />}
              onClick={handleEditGroup}
            >
              Edit Group
            </Button>
          ) : !isMember ? (
            <Button
              variant="contained"
              onClick={() => joinGroupMutation.mutate()}
              disabled={joinGroupMutation.isLoading}
            >
              Join Group
            </Button>
          ) : null}
        </Box>

        <Typography variant="h6" gutterBottom>
          Members
        </Typography>
        <List>
          {groupData.data.members.map((member) => (
            <ListItem key={member._id}>
              <ListItemAvatar>
                <Avatar src={member.avatar} alt={member.username} />
              </ListItemAvatar>
              <ListItemText
                primary={member.username}
                secondary={member._id === groupData.data.admin ? 'Admin' : 'Member'}
              />
            </ListItem>
          ))}
        </List>
      </Paper>

      {isMember && (
        <Paper sx={{ p: 2, mb: 3 }}>
          <Box component="form" onSubmit={handleCreatePost}>
            <TextField
              fullWidth
              multiline
              rows={3}
              placeholder="Share something with the group..."
              value={newPost}
              onChange={(e) => setNewPost(e.target.value)}
              sx={{ mb: 2 }}
            />
            <Button
              variant="contained"
              type="submit"
              disabled={!newPost.trim() || createPostMutation.isLoading}
            >
              Post
            </Button>
          </Box>
        </Paper>
      )}

      <Grid container spacing={3}>
        {groupPosts?.data?.map((post) => (
          <Grid item xs={12} key={post._id}>
            <Card>
              <CardContent>
                <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                  <Avatar
                    src={post.author.avatar}
                    alt={post.author.username}
                    sx={{ mr: 2 }}
                  />
                  <Box>
                    <Typography variant="subtitle1">
                      {post.author.username}
                    </Typography>
                    <Typography variant="caption" color="text.secondary">
                      {new Date(post.createdAt).toLocaleString()}
                    </Typography>
                  </Box>
                </Box>
                <Typography variant="body1" sx={{ mb: 2 }}>
                  {post.content}
                </Typography>
                {post.media && (
                  <Box
                    component="img"
                    src={post.media}
                    alt="Post media"
                    sx={{
                      width: '100%',
                      maxHeight: 400,
                      objectFit: 'cover',
                      borderRadius: 1,
                    }}
                  />
                )}
              </CardContent>
              <Divider />
              <CardActions>
                <IconButton
                  onClick={() => handleLike(post._id)}
                  color={post.likes.includes(currentUser._id) ? 'primary' : 'default'}
                >
                  {post.likes.includes(currentUser._id) ? (
                    <ThumbUpIcon />
                  ) : (
                    <ThumbUpOutlinedIcon />
                  )}
                </IconButton>
                <Typography variant="body2" color="text.secondary">
                  {post.likes.length}
                </Typography>
                <IconButton>
                  <CommentIcon />
                </IconButton>
                <Typography variant="body2" color="text.secondary">
                  {post.comments.length}
                </Typography>
                <IconButton>
                  <ShareIcon />
                </IconButton>
              </CardActions>
            </Card>
          </Grid>
        ))}
      </Grid>

      <Dialog open={editDialogOpen} onClose={() => setEditDialogOpen(false)}>
        <DialogTitle>Edit Group</DialogTitle>
        <DialogContent>
          <TextField
            fullWidth
            label="Group Name"
            value={editForm.name}
            onChange={(e) =>
              setEditForm((prev) => ({ ...prev, name: e.target.value }))
            }
            margin="normal"
          />
          <TextField
            fullWidth
            label="Description"
            value={editForm.description}
            onChange={(e) =>
              setEditForm((prev) => ({ ...prev, description: e.target.value }))
            }
            margin="normal"
            multiline
            rows={4}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setEditDialogOpen(false)}>Cancel</Button>
          <Button
            onClick={handleUpdateGroup}
            variant="contained"
            disabled={updateGroupMutation.isLoading}
          >
            Save
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default Group;
