import React, { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import {
  Box,
  TextField,
  Button,
  List,
  ListItem,
  ListItemAvatar,
  ListItemText,
  Avatar,
  IconButton,
  Paper,
  Typography,
  CircularProgress,
  Alert,
} from '@mui/material';
import {
  Person as PersonIcon,
  PersonAdd as PersonAddIcon,
} from '@mui/icons-material';
import { users } from '../services/api';
import { useAuth } from '../context/AuthContext';

const AddFriend = () => {
  const { user } = useAuth();
  const queryClient = useQueryClient();
  const [searchQuery, setSearchQuery] = useState('');
  const [error, setError] = useState('');

  const { data: searchResults, isLoading, error: searchError } = useQuery({
    queryKey: ['searchUsers', searchQuery],
    queryFn: async () => {
      try {
        console.log('Making search request for query:', searchQuery);
        const response = await users.search(searchQuery);
        console.log('Search API response:', response);
        
        // Check if response exists and has the expected structure
        if (!response || !response.data || !Array.isArray(response.data.data)) {
          console.error('Invalid response structure:', response);
          throw new Error('Invalid response format from server');
        }
        
        return response.data;
      } catch (error) {
        console.error('Search API error:', error);
        console.error('Error details:', {
          message: error.message,
          response: error.response?.data,
          status: error.response?.status
        });
        throw error;
      }
    },
    enabled: searchQuery.length >= 3,
    retry: false,
    onError: (error) => {
      console.error('Search error:', error);
      const errorMessage = error.response?.data?.error || error.message || 'Failed to search users';
      setError(errorMessage);
    }
  });

  const sendRequestMutation = useMutation({
    mutationFn: (userId) => users.sendFriendRequest(userId),
    onSuccess: () => {
      queryClient.invalidateQueries(['friendRequests']);
      setSearchQuery('');
      setError('');
    },
    onError: (error) => {
      console.error('Send request error:', error);
      const errorMessage = error.response?.data?.error || error.message || 'Failed to send friend request';
      setError(errorMessage);
    },
  });

  const handleSearch = (e) => {
    setSearchQuery(e.target.value);
    setError('');
  };

  const handleSendRequest = (userId) => {
    sendRequestMutation.mutate(userId);
  };

  // Update the usersList filtering to use the correct data structure
  const usersList = Array.isArray(searchResults?.data) 
    ? searchResults.data.filter(foundUser => foundUser._id !== user?._id)
    : [];

  console.log('Search results:', searchResults);
  console.log('Users list:', usersList);

  return (
    <Paper sx={{ p: 2, mb: 2 }}>
      <Typography variant="h6" gutterBottom>
        Add Friend
      </Typography>
      <TextField
        fullWidth
        placeholder="Search by username..."
        value={searchQuery}
        onChange={handleSearch}
        sx={{ mb: 2 }}
      />
      {(error || searchError) && (
        <Alert severity="error" sx={{ mb: 2 }}>
          {error || searchError?.message || 'An error occurred'}
        </Alert>
      )}
      {isLoading ? (
        <Box sx={{ display: 'flex', justifyContent: 'center', p: 2 }}>
          <CircularProgress />
        </Box>
      ) : searchQuery.length >= 3 ? (
        <List>
          {usersList.map((foundUser) => (
            <ListItem
              key={foundUser._id}
              secondaryAction={
                <IconButton
                  edge="end"
                  aria-label="add friend"
                  onClick={() => handleSendRequest(foundUser._id)}
                  disabled={sendRequestMutation.isLoading}
                >
                  <PersonAddIcon />
                </IconButton>
              }
            >
              <ListItemAvatar>
                <Avatar
                  src={foundUser.profilePicture ? `http://localhost:5000${foundUser.profilePicture}` : undefined}
                  alt={foundUser.username}
                >
                  <PersonIcon />
                </Avatar>
              </ListItemAvatar>
              <ListItemText
                primary={foundUser.username}
                secondary={foundUser.email}
              />
            </ListItem>
          ))}
          {usersList.length === 0 && searchQuery.length >= 3 && (
            <ListItem>
              <ListItemText primary="No users found" />
            </ListItem>
          )}
        </List>
      ) : searchQuery.length > 0 && searchQuery.length < 3 ? (
        <Typography color="text.secondary" sx={{ p: 2 }}>
          Type at least 3 characters to search
        </Typography>
      ) : null}
    </Paper>
  );
};

export default AddFriend;
