import React from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import {
  Box,
  Paper,
  Typography,
  List,
  ListItem,
  ListItemAvatar,
  ListItemText,
  Avatar,
  IconButton,
  Divider,
  CircularProgress,
  Button,
} from '@mui/material';
import {
  Person as PersonIcon,
  PersonRemove as PersonRemoveIcon,
  Message as MessageIcon,
} from '@mui/icons-material';
import { users } from '../services/api';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import AddFriend from './AddFriend';

const FriendsList = ({ compact }) => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const queryClient = useQueryClient();

  const { data: friendsData, isLoading } = useQuery({
    queryKey: ['friends', user?._id],
    queryFn: () => users.getFriends(user?._id),
    enabled: !!user?._id,
  });

  const removeFriendMutation = useMutation({
    mutationFn: (friendId) => users.removeFriend(user._id, friendId),
    onSuccess: () => {
      queryClient.invalidateQueries(['friends', user?._id]);
    },
  });

  const handleRemoveFriend = (friendId) => {
    removeFriendMutation.mutate(friendId);
  };

  const handleMessage = (friend) => {
    navigate('/chat', {
      state: {
        userId: friend._id,
        username: friend.username,
        profilePicture: friend.profilePicture
      }
    });
  };

  if (isLoading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', p: 2 }}>
        <CircularProgress />
      </Box>
    );
  }

  // Ensure friends is always an array
  const friends = Array.isArray(friendsData?.data?.data) ? friendsData.data.data : [];

  return (
    <>
      {!compact && <AddFriend />}
      <Box sx={{ p: compact ? 0 : 2, pt: compact ? 0 : 2 }}>
        <Typography variant="subtitle1" sx={{ fontWeight: 600, fontSize: compact ? 16 : 18, mb: compact ? 1 : 2, color: '#333' }}>
          Friends{friends.length > 0 ? ` (${friends.length})` : ''}
        </Typography>
        <List sx={{ p: 0 }}>
          {friends.map((friend) => (
            <React.Fragment key={friend._id}>
              <ListItem
                disableGutters
                sx={{
                  py: compact ? 0.5 : 1.5,
                  px: 0,
                  minHeight: 48,
                  display: 'flex',
                  alignItems: 'center',
                  gap: 1,
                }}
                secondaryAction={
                  !compact && (
                    <Box>
                      <IconButton
                        edge="end"
                        aria-label="message"
                        onClick={() => handleMessage(friend)}
                        sx={{ mr: 1 }}
                      >
                        <MessageIcon fontSize="small" />
                      </IconButton>
                      <IconButton
                        edge="end"
                        aria-label="remove"
                        onClick={() => handleRemoveFriend(friend._id)}
                      >
                        <PersonRemoveIcon fontSize="small" />
                      </IconButton>
                    </Box>
                  )
                }
              >
                <ListItemAvatar sx={{ minWidth: 0, mr: 1 }}>
                  <Avatar
                    src={friend.profilePicture ? `http://localhost:5000${friend.profilePicture}` : 'http://localhost:5000/uploads/default-profile.png'}
                    alt={friend.username}
                    sx={{ width: compact ? 32 : 40, height: compact ? 32 : 40, fontSize: compact ? 16 : 20 }}
                  >
                    <PersonIcon fontSize="small" />
                  </Avatar>
                </ListItemAvatar>
                <ListItemText
                  primary={<Typography sx={{ fontWeight: 500, fontSize: compact ? 15 : 16, color: '#222', whiteSpace: 'nowrap', overflow: 'hidden', textOverflow: 'ellipsis' }}>{friend.username}</Typography>}
                  secondary={compact ? null : 'Friend'}
                  sx={{ m: 0 }}
                />
                {compact && (
                  <IconButton
                    edge="end"
                    aria-label="message"
                    onClick={() => handleMessage(friend)}
                    sx={{ ml: 0.5 }}
                  >
                    <MessageIcon fontSize="small" />
                  </IconButton>
                )}
              </ListItem>
              {compact ? <Divider sx={{ ml: 5, mr: 0 }} /> : <Divider variant="inset" component="li" />}
            </React.Fragment>
          ))}
          {friends.length === 0 && (
            <ListItem>
              <ListItemText
                primary={<Typography sx={{ fontSize: compact ? 14 : 16, color: '#888' }}>No friends yet</Typography>}
                secondary={compact ? null : 'Add friends to see them here'}
              />
            </ListItem>
          )}
        </List>
      </Box>
    </>
  );
};

export default FriendsList;
