import React, { useState, useEffect } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import {
  Box,
  TextField,
  Button,
  List,
  ListItem,
  ListItemAvatar,
  ListItemText,
  Avatar,
  IconButton,
  Paper,
  Typography,
  CircularProgress,
  Alert,
} from '@mui/material';
import {
  Person as PersonIcon,
  PersonAdd as PersonAddIcon,
} from '@mui/icons-material';
import { users } from '../services/api';
import { useAuth } from '../context/AuthContext';

const AddFriend = () => {
  const { user } = useAuth();
  const queryClient = useQueryClient();
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  console.log('=== AddFriend Component ===');
  console.log('Auth user:', user);

  const searchMutation = useMutation({
    mutationFn: async (query) => {
      console.log('=== Search Mutation Function ===');
      console.log('Search query:', query);
      console.log('Current auth user:', user);
      const response = await users.search(query);
      console.log('Search mutation response:', response);
      return response;
    },
    onSuccess: (data) => {
      console.log('=== Search Mutation Success ===');
      console.log('Search results data:', data);
      setSearchResults(data);
      setError(null);
    },
    onError: (error) => {
      console.error('=== Search Mutation Error ===');
      console.error('Error details:', {
        message: error.message,
        response: error.response?.data,
        status: error.response?.status
      });
      setError(error.response?.data?.error || 'Error searching users');
    }
  });

  // Debounced search effect
  useEffect(() => {
    const timer = setTimeout(() => {
      if (searchQuery.trim().length >= 3) {
        console.log('=== Debounced Search ===');
        console.log('Search query:', searchQuery);
        console.log('Current auth user:', user);
        setLoading(true);
        searchMutation.mutate(searchQuery, {
          onSettled: () => {
            console.log('=== Search Settled ===');
            setLoading(false);
          }
        });
      }
    }, 500); // 500ms debounce

    return () => clearTimeout(timer);
  }, [searchQuery]);

  const sendRequestMutation = useMutation({
    mutationFn: ({ userId, fromUserId }) => users.sendFriendRequest(userId, fromUserId),
    onSuccess: (data) => {
      console.log('Friend request sent successfully:', data);
      setError(null);
      // Optionally refresh search results
      if (searchQuery) {
        searchMutation.mutate(searchQuery);
      }
    },
    onError: (error) => {
      console.error('Send request error:', error);
      setError(error.response?.data?.error || 'Error sending friend request');
    }
  });

  const handleSendRequest = (userId) => {
    console.log('handleSendRequest called with:', { userId, user });
    const fromUserId = user?.id || 'temp-' + Date.now();
    console.log('Sending friend request with:', { userId, fromUserId });
    sendRequestMutation.mutate({ userId, fromUserId });
  };

  // Filter out current user from search results
  const usersList = searchResults?.data?.filter(searchUser => {
    const isNotCurrentUser = searchUser._id !== user?.id;
    console.log('Filtering user:', {
      searchUserId: searchUser._id,
      currentUserId: user?.id,
      isNotCurrentUser
    });
    return isNotCurrentUser;
  }) || [];
  console.log('=== Search Results Processing ===');
  console.log('Raw search results:', searchResults);
  console.log('Current auth user:', user);
  console.log('Filtered users list:', usersList);

  return (
    <Box sx={{ p: 2 }}>
      <Typography variant="h6" gutterBottom>
        Add Friends
      </Typography>
      <TextField
        fullWidth
        variant="outlined"
        placeholder="Search by username..."
        value={searchQuery}
        onChange={(e) => {
          setSearchQuery(e.target.value);
          setError(null);
        }}
        sx={{ mb: 2 }}
      />
      {(error || searchMutation.error) && (
        <Alert severity="error" sx={{ mb: 2 }}>
          {error || searchMutation.error?.message || 'An error occurred'}
        </Alert>
      )}
      {loading ? (
        <Box sx={{ display: 'flex', justifyContent: 'center', p: 2 }}>
          <CircularProgress />
        </Box>
      ) : searchQuery.length >= 3 ? (
        <List>
          {usersList.map((user) => (
            <ListItem
              key={user._id}
              secondaryAction={
                <IconButton
                  edge="end"
                  aria-label="add friend"
                  onClick={() => handleSendRequest(user._id)}
                  disabled={sendRequestMutation.isLoading}
                >
                  <PersonAddIcon />
                </IconButton>
              }
            >
              <ListItemAvatar>
                <Avatar
                  src={user.profilePicture ? `http://localhost:5000${user.profilePicture}` : undefined}
                  alt={user.username}
                >
                  <PersonIcon />
                </Avatar>
              </ListItemAvatar>
              <ListItemText
                primary={user.username}
                secondary={user.email}
              />
            </ListItem>
          ))}
          {usersList.length === 0 && searchQuery.length >= 3 && (
            <ListItem>
              <ListItemText primary="No users found" />
            </ListItem>
          )}
        </List>
      ) : searchQuery.length > 0 && searchQuery.length < 3 ? (
        <Typography color="text.secondary" sx={{ p: 2 }}>
          Type at least 3 characters to search
        </Typography>
      ) : null}
    </Box>
  );
};

export default AddFriend;
