  Tabs,
  Tab,
  Chip,
  Alert,
} from '@mui/material';
import {
  Edit as EditIcon,
  Delete as DeleteIcon,
  ThumbUp as ThumbUpIcon,
  ThumbUpOutlined as ThumbUpOutlinedIcon,
  Comment as CommentIcon,
  Share as ShareIcon,
  Person as PersonIcon,
  Group as GroupIcon,
} from '@mui/icons-material';
import { groups, posts } from '../services/api';
import { useAuth } from '../context/AuthContext';

const Group = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { user: currentUser } = useAuth();
  const queryClient = useQueryClient();
  const [editDialogOpen, setEditDialogOpen] = useState(false);
  const [deleteDialogOpen, setDeleteDialogOpen] = useState(false);
  const [newPost, setNewPost] = useState('');
  const [activeTab, setActiveTab] = useState(0);
  const [editForm, setEditForm] = useState({
    name: '',
    description: '',
  });

  const { data: groupData, isLoading: groupLoading } = useQuery({
    queryKey: ['group', id],
    queryFn: () => groups.getById(id),
  });

  const { data: groupPosts, isLoading: postsLoading } = useQuery({
    queryKey: ['groupPosts', id],
    queryFn: () => posts.getAll({ group: id }),
  });

  const joinGroupMutation = useMutation({
    mutationFn: () => groups.join(id),
    onSuccess: () => {
      queryClient.invalidateQueries(['group', id]);
    },
  });

  const leaveGroupMutation = useMutation({
    mutationFn: () => groups.leave(id),
    onSuccess: () => {
      queryClient.invalidateQueries(['group', id]);
    },
  });

  const updateGroupMutation = useMutation({
    mutationFn: (data) => groups.update(id, data),
    onSuccess: () => {
      queryClient.invalidateQueries(['group', id]);
      setEditDialogOpen(false);
    },
  });

  const deleteGroupMutation = useMutation({
    mutationFn: () => groups.delete(id),
    onSuccess: () => {
      navigate('/groups');
    },
  });

  const createPostMutation = useMutation({
    mutationFn: (content) => posts.create({ content, group: id }),
    onSuccess: () => {
      queryClient.invalidateQueries(['groupPosts', id]);
      setNewPost('');
    },
  });

  const likePostMutation = useMutation({
    mutationFn: (postId) => posts.like(postId),
    onSuccess: () => {
      queryClient.invalidateQueries(['groupPosts', id]);
    },
  });

  const handleEditGroup = () => {
    setEditForm({
      name: groupData.data.name,
      description: groupData.data.description || '',
    });
    setEditDialogOpen(true);
  };

  const handleUpdateGroup = () => {
    updateGroupMutation.mutate(editForm);
  };

  const handleDeleteGroup = () => {
    deleteGroupMutation.mutate();
  };

  const handleCreatePost = (e) => {
    e.preventDefault();
    if (newPost.trim()) {
      createPostMutation.mutate(newPost);
    }
  };

  const handleLike = (postId) => {
    likePostMutation.mutate(postId);
  };

  const isAdmin = groupData?.data?.admin?._id === currentUser?._id;
  const isMember = groupData?.data?.members?.some(
    (member) => member._id === currentUser?._id
  );

  if (groupLoading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="60vh">
        <CircularProgress />
      </Box>
    );
  }

  if (!groupData?.data) {
    return (
      <Container maxWidth="lg" sx={{ py: 4 }}>
        <Alert severity="error">Group not found</Alert>
      </Container>
    );
  }

  return (
    <Container maxWidth="lg" sx={{ py: 4 }}>
      <Paper sx={{ p: 3, mb: 4 }}>
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', mb: 3 }}>
          <Box>
            <Typography variant="h4" component="h1" gutterBottom>
              {groupData.data.name}
            </Typography>
            <Typography color="text.secondary" paragraph>
              {groupData.data.description || 'No description provided'}
            </Typography>
            <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
              <Chip
                icon={<GroupIcon />}
                label={`${groupData.data.members.length} members`}
                variant="outlined"
              />
              <Chip
                icon={<PersonIcon />}
                label={`Admin: ${groupData.data.admin.username}`}
                variant="outlined"
              />
            </Box>
          </Box>
          <Box>
            {isAdmin ? (
              <>
                <IconButton onClick={handleEditGroup} sx={{ mr: 1 }}>
                  <EditIcon />
                </IconButton>
                <IconButton onClick={() => setDeleteDialogOpen(true)} color="error">
                  <DeleteIcon />
                </IconButton>
              </>
            ) : isMember ? (
              <Button
                variant="outlined"
                color="error"
                onClick={() => leaveGroupMutation.mutate()}
                disabled={leaveGroupMutation.isLoading}
              >
                Leave Group
              </Button>
            ) : (
              <Button
                variant="contained"
                onClick={() => joinGroupMutation.mutate()}
                disabled={joinGroupMutation.isLoading}
              >
                Join Group
              </Button>
            )}
          </Box>
        </Box>
      </Paper>

      <Tabs
        value={activeTab}
        onChange={(e, newValue) => setActiveTab(newValue)}
        sx={{ mb: 3 }}
      >
        <Tab label="Posts" />
        <Tab label="Members" />
      </Tabs>

      {activeTab === 0 && (
        <>
          {isMember && (
            <Paper sx={{ p: 2, mb: 3 }}>
              <Box component="form" onSubmit={handleCreatePost}>
                <TextField
                  fullWidth
                  multiline
                  rows={3}
                  placeholder="Share something with the group..."
                  value={newPost}
                  onChange={(e) => setNewPost(e.target.value)}
                  sx={{ mb: 2 }}
                />
                <Button
                  variant="contained"
                  type="submit"
                  disabled={!newPost.trim() || createPostMutation.isLoading}
                >
                  Post
                </Button>
              </Box>
            </Paper>
          )}

          {postsLoading ? (
            <Box display="flex" justifyContent="center" p={3}>
              <CircularProgress />
            </Box>
          ) : (
            <Grid container spacing={3}>
              {groupPosts?.data?.map((post) => (
                <Grid item xs={12} key={post._id}>
                  <Card>
                    <CardContent>
                      <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                        <Avatar
                          src={post.author.profilePicture}
                          alt={post.author.username}
                          sx={{ mr: 2 }}
                        />
                        <Box>
                          <Typography variant="subtitle1">
                            {post.author.username}
                          </Typography>
                          <Typography variant="caption" color="text.secondary">
                            {new Date(post.createdAt).toLocaleDateString()}
                          </Typography>
                        </Box>
                      </Box>
                      <Typography paragraph>{post.content}</Typography>
                      {post.media && (
                        <Box sx={{ mb: 2 }}>
                          <img
                            src={post.media}
                            alt="Post media"
                            style={{ maxWidth: '100%', borderRadius: 8 }}
                          />
                        </Box>
                      )}
                    </CardContent>
                    <Divider />
                    <CardActions>
                      <IconButton
                        onClick={() => handleLike(post._id)}
                        color={post.likes.includes(currentUser?._id) ? 'primary' : 'default'}
                      >
                        {post.likes.includes(currentUser?._id) ? (
                          <ThumbUpIcon />
                        ) : (
                          <ThumbUpOutlinedIcon />
                        )}
                      </IconButton>
                      <Typography variant="body2" color="text.secondary">
                        {post.likes.length} likes
                      </Typography>
                      <IconButton>
                        <CommentIcon />
                      </IconButton>
                      <Typography variant="body2" color="text.secondary">
                        {post.comments.length} comments
                      </Typography>
                      <IconButton>
                        <ShareIcon />
                      </IconButton>
                    </CardActions>
                  </Card>
                </Grid>
              ))}
            </Grid>
          )}
        </>
      )}

      {activeTab === 1 && (
        <Paper sx={{ p: 2 }}>
          <List>
            {groupData.data.members.map((member) => (
              <ListItem
                key={member._id}
                secondaryAction={
                  member._id === groupData.data.admin._id && (
                    <Chip label="Admin" color="primary" size="small" />
                  )
                }
              >
                <ListItemAvatar>
                  <Avatar src={member.profilePicture} alt={member.username} />
                </ListItemAvatar>
                <ListItemText
                  primary={member.username}
                  secondary={member._id === groupData.data.admin._id ? 'Group Admin' : 'Member'}
                />
              </ListItem>
            ))}
          </List>
        </Paper>
      )}

      <Dialog open={editDialogOpen} onClose={() => setEditDialogOpen(false)}>
        <DialogTitle>Edit Group</DialogTitle>
        <DialogContent>
          <TextField
            fullWidth
            label="Group Name"
            value={editForm.name}
            onChange={(e) =>
              setEditForm((prev) => ({ ...prev, name: e.target.value }))
            }
            margin="normal"
          />
          <TextField
            fullWidth
            label="Description"
            value={editForm.description}
            onChange={(e) =>
              setEditForm((prev) => ({ ...prev, description: e.target.value }))
            }
            margin="normal"
            multiline
            rows={4}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setEditDialogOpen(false)}>Cancel</Button>
          <Button
            onClick={handleUpdateGroup}
            variant="contained"
            disabled={updateGroupMutation.isLoading}
          >
            Save
          </Button>
        </DialogActions>
      </Dialog>

      <Dialog open={deleteDialogOpen} onClose={() => setDeleteDialogOpen(false)}>
        <DialogTitle>Delete Group</DialogTitle>
        <DialogContent>
          <Typography>
            Are you sure you want to delete this group? This action cannot be undone.
          </Typography>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setDeleteDialogOpen(false)}>Cancel</Button>
          <Button
            onClick={handleDeleteGroup}
            color="error"
            variant="contained"
            disabled={deleteGroupMutation.isLoading}
          >
            Delete
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default Group;
